import { GoogleGenAI } from "@google/genai";
import { ArticleData } from "../types";

const apiKey = process.env.API_KEY || '';

// Initialize the client
const ai = new GoogleGenAI({ apiKey });

/**
 * Fetches and reconstructs an article using Gemini with Google Search Grounding.
 * This effectively bypasses friction by finding the content via search index.
 */
export const fetchArticleContent = async (url: string): Promise<ArticleData> => {
  if (!apiKey) {
    throw new Error("API Key is missing. Please set process.env.API_KEY.");
  }

  const modelId = "gemini-2.5-flash"; // Use flash for speed and search capability

  // Prompt designed to be robust against "No content" errors by forcing a JSON output even on failure
  const prompt = `
    TASK: Retrieve the full content of the article at: ${url}

    You are an expert at finding content behind paywalls using Google Search.

    STRATEGY:
    1. Search for the URL directly.
    2. Search for the *Title* of the article (deduced from the URL slug) to find syndicated versions on free platforms (e.g., MSN, Yahoo Finance, LinkedIn articles, or re-blogs).
    3. Search for "archive.is [URL]" or "cache:[URL]" to find preserved full-text versions.
    4. If the full text is unavailable, compile a detailed, long-form report (at least 500 words) summarizing the key points, arguments, and data from available snippets, reviews, and discussion threads.

    CRITICAL INSTRUCTION:
    - Do NOT return an empty response. 
    - If you cannot find the article, you must generate a summary of what you *can* find about the topic/title.

    OUTPUT FORMAT:
    Return the output strictly as a JSON object inside a JSON code block. 

    \`\`\`json
    {
      "title": "Article Title",
      "author": "Author Name (or 'Unknown')",
      "siteName": "Source Website",
      "content": "# Full Markdown Content Here..."
    }
    \`\`\`
  `;

  try {
    const response = await ai.models.generateContent({
      model: modelId,
      contents: prompt,
      config: {
        tools: [{ googleSearch: {} }],
        // responseMimeType: "application/json" is NOT supported with googleSearch
      },
    });

    let responseText = response.text;

    // Fallback: sometimes the model output is in parts and .text getter might miss it if structured weirdly with tools (unlikely but safe)
    if (!responseText && response.candidates && response.candidates.length > 0) {
        const parts = response.candidates[0].content?.parts;
        if (parts) {
            responseText = parts
                .filter((p: any) => p.text)
                .map((p: any) => p.text)
                .join('');
        }
    }

    if (!responseText) {
        console.error("Empty response from Gemini:", JSON.stringify(response, null, 2));
        throw new Error("AI returned no content. The article might be blocked or inaccessible.");
    }

    // Manual JSON extraction
    // 1. Try to find content within ```json ... ``` code blocks
    const jsonMatch = responseText.match(/```json\n([\s\S]*?)\n```/) || responseText.match(/```\n([\s\S]*?)\n```/);
    let jsonString = jsonMatch ? jsonMatch[1] : responseText;

    // 2. Fallback: Try to find the first '{' and last '}'
    const firstBrace = jsonString.indexOf('{');
    const lastBrace = jsonString.lastIndexOf('}');
    if (firstBrace !== -1 && lastBrace !== -1) {
        jsonString = jsonString.substring(firstBrace, lastBrace + 1);
    }

    let data;
    try {
        data = JSON.parse(jsonString);
    } catch (parseError) {
        console.error("Failed to parse JSON response:", responseText);
        // Fallback: If JSON parsing fails, assume the entire text is the content
        // This often happens if the model refuses to use JSON but returns the text.
        data = {
            title: "Article Content",
            content: responseText, // Use the raw response as content
            author: "Unknown",
            siteName: "Unknown"
        };
    }

    // Extract grounding sources if available
    const sources = response.candidates?.[0]?.groundingMetadata?.groundingChunks?.map((chunk: any) => {
        return chunk.web ? { title: chunk.web.title, uri: chunk.web.uri } : null;
    }).filter((item: any) => item !== null) || [];

    return {
      title: data.title || "Untitled Article",
      content: data.content || "Could not retrieve content.",
      author: data.author,
      siteName: data.siteName,
      url: url,
      sources: sources
    };

  } catch (error: any) {
    console.error("Error fetching article:", error);
    // Enhance error message for the user
    throw new Error(error.message || "Failed to retrieve article. It might be inaccessible.");
  }
};

