import { GoogleGenAI } from "@google/genai";
import { ArticleData } from "../types";

const apiKey = process.env.API_KEY || '';

// Initialize the client
const ai = new GoogleGenAI({ apiKey });

/**
 * Fetches and reconstructs an article using Gemini with Google Search Grounding.
 * This effectively bypasses friction by finding the content via search index.
 */
export const fetchArticleContent = async (url: string): Promise<ArticleData> => {
  if (!apiKey) {
    throw new Error("API Key is missing. Please set process.env.API_KEY.");
  }

  const modelId = "gemini-2.5-flash"; // Use flash for speed and search capability

  const prompt = `
    ROLE: You are an advanced Content Retrieval and Reconstruction Agent.
    GOAL: Provide the user with the *readable content* of the link provided, regardless of paywalls or clutter.

    TARGET URL: ${url}

    **EXECUTION PROTOCOL:**
    1.  **Aggressive Search**: Use the 'googleSearch' tool to find the article content.
        -   Search for the exact URL.
        -   Search for the *Title* + "full text".
        -   Search for the *Title* + "archive".
        -   Search for the *Author* and *Topic* to find cross-posts (e.g., on LinkedIn, Substack, MSN, Yahoo Finance).

    2.  **Content Synthesis (Crucial)**:
        -   **Scenario A (Full Text Found)**: If you find the full text in a cache or syndicated copy, format it in clean Markdown.
        -   **Scenario B (Paywalled)**: If the direct text is blocked, you MUST **reconstruct** the article.
            -   Combine all search snippets, previews, and your internal knowledge of the specific article/topic.
            -   **DO NOT** return a short summary.
            -   **DO NOT** return just the title.
            -   **GENERATE A FULL-LENGTH ARTICLE** (aim for 800-1500 words) that mirrors the structure, arguments, and depth of the original.
            -   Use headers, bullet points, and detailed paragraphs.

    **OUTPUT FORMAT:**
    Return ONLY a valid JSON object.
    -   The "content" field must be a long Markdown string.
    -   Escape all double quotes inside the content string properly.
    -   Escape all newlines in the content string as \\n.

    \`\`\`json
    {
      "title": "Exact Article Title",
      "author": "Author Name",
      "siteName": "Publication Name",
      "content": "# Title\\n\\n## Introduction\\n\\n[Full reconstructed text here...]"
    }
    \`\`\`
  `;

  try {
    const response = await ai.models.generateContent({
      model: modelId,
      contents: prompt,
      config: {
        tools: [{ googleSearch: {} }],
        // responseMimeType: "application/json" // Incompatible with tools, we parse manually
      },
    });

    let responseText = response.text;

    // Fallback: sometimes the model output is in parts and .text getter might miss it if structured weirdly with tools
    if (!responseText && response.candidates && response.candidates.length > 0) {
        const parts = response.candidates[0].content?.parts;
        if (parts) {
            responseText = parts
                .filter((p: any) => p.text)
                .map((p: any) => p.text)
                .join('');
        }
    }

    if (!responseText) {
        console.error("Empty response from Gemini:", JSON.stringify(response, null, 2));
        throw new Error("AI returned no content. The article might be totally inaccessible.");
    }

    // Manual JSON extraction
    // 1. Try to find content within ```json ... ``` code blocks
    const jsonMatch = responseText.match(/```json\n([\s\S]*?)\n```/) || responseText.match(/```\n([\s\S]*?)\n```/);
    let jsonString = jsonMatch ? jsonMatch[1] : responseText;

    // 2. Cleanup: find the first '{' and last '}'
    const firstBrace = jsonString.indexOf('{');
    const lastBrace = jsonString.lastIndexOf('}');

    if (firstBrace !== -1 && lastBrace !== -1) {
        jsonString = jsonString.substring(firstBrace, lastBrace + 1);
    } else {
         // If no braces found, the model likely failed to output JSON and just output text.
         // Treat the whole text as content.
         return {
            title: "Article View",
            content: responseText,
            author: "AI Reader",
            siteName: "Gemini",
            url: url
         };
    }

    let data;
    try {
        // Attempt to parse
        data = JSON.parse(jsonString);
    } catch (parseError) {
        console.warn("Initial JSON parse failed, attempting to sanitize...", parseError);

        // Sanitize: Sometimes models output unescaped newlines inside strings which breaks JSON.parse
        // This is a naive heuristic fix for common LLM JSON errors
        try {
            // 1. escape control characters if necessary (complex to do perfectly with regex)
            // 2. If it failed, we might just fallback to using the text.
            data = JSON.parse(jsonString.replace(/\n/g, "\\n")); 
        } catch (e) {
            // Ultimate fallback: Just treat the raw string as the content
             return {
                title: "Generated Article",
                content: responseText.replace(/```json/g, '').replace(/```/g, ''),
                author: "Unknown",
                siteName: "Reader",
                url: url
             };
        }
    }

    // Check if content is suspiciously short (e.g. just the title)
    if (data.content && data.content.length < 200) {
        // If the JSON is valid but content is empty, the model was lazy.
        // We return the raw text if it exists elsewhere, or a specific error message
        // forcing the user to maybe try again? Or we just display what we have.
        // Let's assume the model failed the reconstruction instruction.
         data.content += "\n\n(Note: The AI could not fully reconstruct this article. It may be heavily protected.)";
    }

    // Extract grounding sources
    const sources = response.candidates?.[0]?.groundingMetadata?.groundingChunks?.map((chunk: any) => {
        return chunk.web ? { title: chunk.web.title, uri: chunk.web.uri } : null;
    }).filter((item: any) => item !== null) || [];

    return {
      title: data.title || "Untitled Article",
      content: data.content || "Could not retrieve content.",
      author: data.author,
      siteName: data.siteName,
      url: url,
      sources: sources
    };

  } catch (error: any) {
    console.error("Error fetching article:", error);
    throw new Error(error.message || "Failed to retrieve article. It might be inaccessible.");
  }
};

