import { ArticleData } from "../types";

// The specific Google Sheet ID provided
const SPREADSHEET_ID = '1_lgKsUJeSx9B1EpAsvp-epON40WvF-iPjznAXklM4Fg';

// GLOBAL CONFIGURATION
// To make this work, you need a Client ID from Google Cloud Console (https://console.cloud.google.com/)
// 1. Create a project
// 2. Enable "Google Sheets API"
// 3. Create OAuth 2.0 Client ID (Web Application)
// 4. Add your domain (or localhost) to "Authorized JavaScript origins"
const CLIENT_ID = process.env.GOOGLE_CLIENT_ID || ''; 
const API_KEY = process.env.API_KEY || ''; // Using the same key if it has Sheets scope, or use a separate one.

// Scopes needed
const SCOPES = 'https://www.googleapis.com/auth/spreadsheets';

declare var gapi: any;
declare var google: any;

let tokenClient: any;
let gapiInited = false;
let gisInited = false;

/**
 * Initialize the Google API Client
 */
export const initializeGoogleApi = async () => {
  if (gapiInited && gisInited) return;

  return new Promise<void>((resolve, reject) => {
    // Load GAPI
    gapi.load('client', async () => {
      try {
        await gapi.client.init({
          apiKey: API_KEY,
          discoveryDocs: ['https://sheets.googleapis.com/$discovery/rest?version=v4'],
        });
        gapiInited = true;

        // Load GIS (Identity Services)
        tokenClient = google.accounts.oauth2.initTokenClient({
          client_id: CLIENT_ID,
          scope: SCOPES,
          callback: '', // defined at request time
        });
        gisInited = true;
        resolve();
      } catch (err) {
        console.error("Error initializing Google API", err);
        reject(err);
      }
    });
  });
};

/**
 * Extracts links from Markdown content in the format [Text](URL)
 */
const extractLinksFromMarkdown = (markdown: string): string => {
  const regex = /\[([^\]]+)\]\(([^)]+)\)/g;
  const links: string[] = [];
  let match;

  while ((match = regex.exec(markdown)) !== null) {
    // match[1] is Text, match[2] is URL
    links.push(`${match[1]}: ${match[2]}`);
  }

  // Also add source links if they exist in the text but aren't markdown formatted? 
  // For now, just the markdown ones as they are most relevant in the reconstructed article.

  if (links.length === 0) return "No links found";
  return links.join('\n');
};

/**
 * Main function to save article to sheet
 */
export const saveArticleToSheet = async (article: ArticleData): Promise<void> => {
  if (!CLIENT_ID) {
    const userClientId = window.prompt("Google Client ID is missing. Please paste your OAuth 2.0 Client ID to enable Sheets integration:");
    if (!userClientId) throw new Error("Client ID is required to save to Sheets.");
    // Temporarily re-init with user provided ID for this session
    tokenClient = google.accounts.oauth2.initTokenClient({
      client_id: userClientId,
      scope: SCOPES,
      callback: '',
    });
  }

  if (!gapiInited) await initializeGoogleApi();

  return new Promise((resolve, reject) => {
    tokenClient.callback = async (resp: any) => {
      if (resp.error) {
        reject(resp);
      }

      try {
        const linksText = extractLinksFromMarkdown(article.content);

        // Prepare row data: Title, Original Link, Full article, Links in the Article
        const values = [
          [
            article.title,
            article.url,
            article.content,
            linksText
          ]
        ];

        const response = await gapi.client.sheets.spreadsheets.values.append({
          spreadsheetId: SPREADSHEET_ID,
          range: 'Sheet1!A:D', // Assuming Sheet1 and appending to columns A-D
          valueInputOption: 'USER_ENTERED',
          resource: {
            values,
          },
        });

        console.log('Sheet append response', response);
        resolve();
      } catch (err) {
        console.error("Error appending to sheet", err);
        reject(err);
      }
    };

    // Request token (triggers popup if needed)
    if (gapi.client.getToken() === null) {
      tokenClient.requestAccessToken({prompt: 'consent'});
    } else {
      tokenClient.requestAccessToken({prompt: ''});
    }
  });
};

