// 2. Enable "Google Sheets API"
// 3. Create OAuth 2.0 Client ID (Web Application)
// 4. Add your domain (or localhost) to "Authorized JavaScript origins"

// We use a let variable so it can be updated at runtime if missing
let activeClientId = process.env.GOOGLE_CLIENT_ID || ''; 
const API_KEY = process.env.API_KEY || ''; 

// Scopes needed
const SCOPES = 'https://www.googleapis.com/auth/spreadsheets';

declare var gapi: any;
declare var google: any;

let tokenClient: any;
let gapiInited = false;

/**
 * Initialize the Google API Client
 * @param clientId Optional client ID to use if initializing late
 */
export const initializeGoogleApi = async (clientId?: string) => {
  if (clientId) {
      activeClientId = clientId;
  }

  return new Promise<void>((resolve, reject) => {
    // Ensure google scripts are loaded
    if (typeof gapi === 'undefined' || typeof google === 'undefined') {
        reject(new Error("Google API scripts not loaded"));
        return;
    }

    gapi.load('client', async () => {
      try {
        // 1. Initialize GAPI (if not already done)
        if (!gapiInited) {
            await gapi.client.init({
            apiKey: API_KEY,
            discoveryDocs: ['https://sheets.googleapis.com/$discovery/rest?version=v4'],
            });
            gapiInited = true;
        }

        // 2. Initialize GIS Token Client (if we have an ID)
        // We re-create this if activeClientId changes or if it wasn't created yet
        if (activeClientId) {
            tokenClient = google.accounts.oauth2.initTokenClient({
                client_id: activeClientId,
                scope: SCOPES,
                callback: '', // defined at request time
            });
        }

        resolve();
      } catch (err) {
        console.error("Error initializing Google API", err);
        reject(err);
      }
    });
  });
};

