import { saveArticleToSheet } from '../services/sheetService';

interface ArticleViewProps {
  article: ArticleData;
  theme: ReaderTheme;
  onBack: () => void;
}

export const ArticleView: React.FC<ArticleViewProps> = ({ article, theme, onBack }) => {
  const [showChat, setShowChat] = useState(false);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [isAsking, setIsAsking] = useState(false);

  // Sheet saving states
  const [isSaving, setIsSaving] = useState(false);
  const [saveSuccess, setSaveSuccess] = useState(false);

  // Copy state
  const [copied, setCopied] = useState(false);

  // Theme configuration
  const themeClasses = {
    [ReaderTheme.LIGHT]: 'bg-white text-gray-900 selection:bg-yellow-200',
    [ReaderTheme.DARK]: 'bg-gray-900 text-gray-300 selection:bg-blue-900',
    [ReaderTheme.SEPIA]: 'bg-sepia-50 text-sepia-900 selection:bg-sepia-200',
  };

  const proseClasses = {
    [ReaderTheme.LIGHT]: 'prose-stone prose-lg',
    [ReaderTheme.DARK]: 'prose-invert prose-lg',
    [ReaderTheme.SEPIA]: 'prose-stone prose-lg marker:text-sepia-800 prose-headings:text-sepia-900 prose-a:text-sepia-800',
  };

  const handleAsk = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!question.trim()) return;

    setIsAsking(true);
    setAnswer('');
    try {
        const result = await askQuestionAboutArticle(article.content, question);
        setAnswer(result);
    } catch (err) {
        setAnswer("Failed to get answer.");
    } finally {
        setIsAsking(false);
    }
  };

  const handleSaveToSheet = async () => {
    setIsSaving(true);
    try {
        await saveArticleToSheet(article);
        setSaveSuccess(true);
        setTimeout(() => setSaveSuccess(false), 3000);
    } catch (error: any) {
        console.error(error);
        if (error.message === 'CANCELLED_BY_USER') {
            // User cancelled the prompt, do nothing
            return;
        }
        alert("Failed to save to Google Sheet. " + (error.message || "Ensure popups are allowed and Client ID is configured."));
    } finally {
        setIsSaving(false);
    }
  };

  const handleCopy = async () => {
    try {
        const textToCopy = `# ${article.title}\n\n${article.content}`;
        await navigator.clipboard.writeText(textToCopy);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
    } catch (err) {
        console.error('Failed to copy', err);
    }
  };

  const handleExportPDF = () => {
    // 1. Generate filename: first 6 words
    const words = article.title.split(/\s+/).filter(w => w.length > 0);
    const filenameBase = words.slice(0, 6).join(' ');

    // 2. Temporarily change document title to control PDF filename
    const originalTitle = document.title;
    document.title = filenameBase;

    // 3. Trigger print
    window.print();

    // 4. Restore original title
    document.title = originalTitle;
  };

  return (
