import React, { useState } from 'react';
import ReactMarkdown from 'react-markdown';
import { ArticleData, ReaderTheme } from '../types';
import { ArrowLeft, BookOpen, ExternalLink, MessageSquare, FileSpreadsheet, Check, Loader2, Copy, Download } from 'lucide-react';
import { askQuestionAboutArticle } from '../services/geminiService';
import { saveArticleToSheet } from '../services/sheetService';

interface ArticleViewProps {
  article: ArticleData;
  theme: ReaderTheme;
  onBack: () => void;
}

export const ArticleView: React.FC<ArticleViewProps> = ({ article, theme, onBack }) => {
  const [showChat, setShowChat] = useState(false);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [isAsking, setIsAsking] = useState(false);

  // Sheet saving states
  const [isSaving, setIsSaving] = useState(false);
  const [saveSuccess, setSaveSuccess] = useState(false);

  // Copy state
  const [copied, setCopied] = useState(false);

  // Theme configuration
  const themeClasses = {
    [ReaderTheme.LIGHT]: 'bg-white text-gray-900 selection:bg-yellow-200',
    [ReaderTheme.DARK]: 'bg-gray-900 text-gray-300 selection:bg-blue-900',
    [ReaderTheme.SEPIA]: 'bg-sepia-50 text-sepia-900 selection:bg-sepia-200',
  };

  const proseClasses = {
    [ReaderTheme.LIGHT]: 'prose-stone prose-lg',
    [ReaderTheme.DARK]: 'prose-invert prose-lg',
    [ReaderTheme.SEPIA]: 'prose-stone prose-lg marker:text-sepia-800 prose-headings:text-sepia-900 prose-a:text-sepia-800',
  };

  const handleAsk = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!question.trim()) return;

    setIsAsking(true);
    setAnswer('');
    try {
        const result = await askQuestionAboutArticle(article.content, question);
        setAnswer(result);
    } catch (err) {
        setAnswer("Failed to get answer.");
    } finally {
        setIsAsking(false);
    }
  };

  const handleSaveToSheet = async () => {
    setIsSaving(true);
    try {
        await saveArticleToSheet(article);
        setSaveSuccess(true);
        setTimeout(() => setSaveSuccess(false), 3000);
    } catch (error: any) {
        console.error(error);
        if (error.message === 'CANCELLED_BY_USER') {
            // User cancelled the prompt, do nothing
            return;
        }
        alert("Failed to save to Google Sheet. " + (error.message || "Ensure popups are allowed and Client ID is configured."));
    } finally {
        setIsSaving(false);
    }
  };

  const handleCopy = async () => {
    try {
        const textToCopy = `# ${article.title}\n\n${article.content}`;
        await navigator.clipboard.writeText(textToCopy);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
    } catch (err) {
        console.error('Failed to copy', err);
    }
  };

  const handleExportPDF = () => {
    // 1. Generate filename: first 6 words
    const words = article.title.split(/\s+/).filter(w => w.length > 0);
    const filenameBase = words.slice(0, 6).join(' ');

    // 2. Temporarily change document title to control PDF filename
    const originalTitle = document.title;
    document.title = filenameBase;

    // 3. Trigger print
    window.print();

    // 4. Restore original title
    document.title = originalTitle;
  };

  return (
    <div className={`min-h-screen transition-colors duration-500 ${themeClasses[theme]}`}>
      {/* Navbar for Reader */}
      <div className={`sticky top-0 z-20 backdrop-blur-md border-b print:hidden ${
          theme === ReaderTheme.DARK ? 'border-gray-800 bg-gray-900/80' : 
          theme === ReaderTheme.SEPIA ? 'border-sepia-200 bg-sepia-50/80' : 'border-gray-200 bg-white/80'
      }`}>
        <div className="max-w-4xl mx-auto px-4 h-16 flex items-center justify-between">
          <button 
            onClick={onBack}
            className="flex items-center space-x-2 opacity-70 hover:opacity-100 transition-opacity font-medium"
          >
            <ArrowLeft size={20} />
            <span>Back</span>
          </button>

          <div className="flex items-center space-x-2 md:space-x-4">
             <button
                onClick={handleCopy}
                className={`p-2 rounded-lg transition-colors ${
                    copied 
                    ? 'text-green-600 bg-green-50' 
                    : 'opacity-70 hover:opacity-100 hover:bg-black/5 dark:hover:bg-white/10'
                }`}
                title="Copy Article Markdown"
             >
                {copied ? <Check size={20} /> : <Copy size={20} />}
             </button>

             <button
                onClick={handleExportPDF}
                className="p-2 rounded-lg opacity-70 hover:opacity-100 hover:bg-black/5 dark:hover:bg-white/10 transition-colors"
                title="Export as PDF"
             >
                <Download size={20} />
             </button>

             <button
                onClick={() => setShowChat(!showChat)}
                className={`p-2 rounded-lg transition-colors ${
                    showChat 
                    ? 'bg-blue-500 text-white' 
                    : 'opacity-70 hover:opacity-100 hover:bg-black/5 dark:hover:bg-white/10'
                }`}
                title="Ask AI"
             >
                <MessageSquare size={20} />
             </button>
             <a 
                href={article.url} 
                target="_blank" 
                rel="noreferrer"
                className="p-2 rounded-lg opacity-70 hover:opacity-100 hover:bg-black/5 dark:hover:bg-white/10 transition-all"
                title="Open Original"
             >
               <ExternalLink size={20} />
             </a>
          </div>
        </div>
      </div>

      <main className="max-w-3xl mx-auto px-6 py-12 animate-fade-in-up">
        {/* PDF-Only Header (Hidden in Browser) - This starts the PDF as requested */}
        <div className="hidden print:block mb-8 pb-4 border-b border-gray-200 text-sm text-blue-600 font-sans break-all">
          <span className="font-bold text-gray-500 mr-2">Original Article:</span>
          {article.url}
        </div>

        {/* Header Info */}
        <header className="mb-12 border-b border-opacity-20 pb-8 border-current">

          {/* Action Buttons Row */}
          <div className="mb-8 flex flex-wrap gap-3 print:hidden">
            <button
                onClick={handleSaveToSheet}
                disabled={isSaving || saveSuccess}
                className={`flex items-center gap-2 px-4 py-2 rounded-full text-sm font-medium transition-all ${
                    saveSuccess 
                        ? 'bg-green-100 text-green-700 border border-green-200'
                        : theme === ReaderTheme.DARK
                            ? 'bg-gray-800 hover:bg-gray-700 border border-gray-700 text-white'
                            : 'bg-white hover:bg-gray-50 border border-gray-200 shadow-sm text-gray-700'
                }`}
            >
                {isSaving ? (
                    <Loader2 size={16} className="animate-spin" />
                ) : saveSuccess ? (
                    <Check size={16} />
                ) : (
                    <FileSpreadsheet size={16} className="text-green-600" />
                )}
                <span>
                    {isSaving ? 'Saving...' : saveSuccess ? 'Saved to Sheet' : 'Save to Google Sheet'}
                </span>
            </button>

            {/* Prominent PDF Export Button */}
            <button
                onClick={handleExportPDF}
                className={`flex items-center gap-2 px-4 py-2 rounded-full text-sm font-medium transition-all ${
                    theme === ReaderTheme.DARK
                        ? 'bg-gray-800 hover:bg-gray-700 border border-gray-700 text-white'
                        : 'bg-white hover:bg-gray-50 border border-gray-200 shadow-sm text-gray-700'
                }`}
            >
                <Download size={16} className="text-blue-500" />
                <span>Export as PDF</span>
            </button>
          </div>

          <h1 className={`text-4xl md:text-5xl font-serif font-bold mb-6 leading-tight tracking-tight`}>
            {article.title}
          </h1>
          <div className="flex flex-wrap items-center gap-4 text-sm font-medium opacity-70 font-sans uppercase tracking-wider">
            {article.author && (
                <div className="flex items-center">
                    <span>By {article.author}</span>
                </div>
            )}
            {article.siteName && (
                <>
                    <span></span>
                    <span>{article.siteName}</span>
                </>
            )}
            <span className="print:hidden"></span>
            <span className="flex items-center gap-1 print:hidden">
                <BookOpen size={14} />
                AI Enhanced
            </span>
          </div>
        </header>

        {/* AI Chat Drawer */}
        {showChat && (
            <div className={`mb-10 p-6 rounded-2xl shadow-lg border print:hidden ${
                theme === ReaderTheme.DARK ? 'bg-gray-800 border-gray-700' : 
                theme === ReaderTheme.SEPIA ? 'bg-sepia-100 border-sepia-200' : 'bg-blue-50 border-blue-100'
            }`}>
                <h3 className="font-bold mb-4 flex items-center gap-2">
                    <MessageSquare size={18} />
                    Ask Gemini about this article
                </h3>
                <form onSubmit={handleAsk} className="flex gap-2 mb-4">
                    <input 
                        type="text" 
                        value={question}
                        onChange={(e) => setQuestion(e.target.value)}
                        placeholder="e.g. What is the main argument?"
                        className={`flex-1 px-4 py-2 rounded-lg border focus:ring-2 focus:outline-none ${
                            theme === ReaderTheme.DARK 
                            ? 'bg-gray-700 border-gray-600 focus:ring-blue-500' 
                            : 'bg-white border-gray-300 focus:ring-blue-400'
                        }`}
                    />
                    <button 
                        type="submit" 
                        disabled={isAsking}
                        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg disabled:opacity-50 font-medium"
                    >
                        {isAsking ? '...' : 'Ask'}
                    </button>
                </form>
                {answer && (
                    <div className={`p-4 rounded-lg text-sm leading-relaxed ${
                         theme === ReaderTheme.DARK ? 'bg-gray-900' : 'bg-white/50'
                    }`}>
                        <ReactMarkdown>{answer}</ReactMarkdown>
                    </div>
                )}
            </div>
        )}

        {/* Main Content */}
        <article className={`prose ${proseClasses[theme]} max-w-none font-serif pb-20`}>
          <ReactMarkdown 
            components={{
              h1: ({node, ...props}) => <h2 className="text-3xl font-bold mt-12 mb-6" {...props} />,
              h2: ({node, ...props}) => <h2 className="text-2xl font-bold mt-10 mb-5" {...props} />,
              p: ({node, ...props}) => <p className="mb-6 leading-8 text-xl" {...props} />,
              a: ({node, ...props}) => (
                <a 
                    className={`underline decoration-2 underline-offset-2 transition-colors ${
                        theme === ReaderTheme.DARK ? 'text-blue-400 decoration-blue-400/30 hover:decoration-blue-400' : 
                        theme === ReaderTheme.SEPIA ? 'text-sepia-900 decoration-sepia-900/30 hover:decoration-sepia-900 font-medium' : 
                        'text-blue-700 decoration-blue-700/30 hover:decoration-blue-700'
                    }`} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    {...props} 
                />
              ),
              blockquote: ({node, ...props}) => (
                <blockquote className={`border-l-4 pl-6 italic my-8 ${
                    theme === ReaderTheme.SEPIA ? 'border-sepia-400 text-sepia-800' : 'border-blue-500 opacity-80'
                }`} {...props} />
              ),
              img: ({node, ...props}) => (
                 // eslint-disable-next-line jsx-a11y/alt-text
                 <img className="rounded-xl shadow-md my-8 w-full object-cover max-h-[500px]" {...props} />
              )
            }}
          >
            {article.content}
          </ReactMarkdown>
        </article>

        {/* Sources Footer */}
        {article.sources && article.sources.length > 0 && (
            <div className={`mt-12 pt-8 border-t print:hidden ${theme === ReaderTheme.DARK ? 'border-gray-800' : 'border-gray-200'}`}>
                <h4 className="text-sm font-bold uppercase tracking-wider opacity-60 mb-4">Sources Found</h4>
                <ul className="space-y-2">
                    {article.sources.map((source, idx) => (
                        <li key={idx}>
                            <a href={source.uri} target="_blank" rel="noreferrer" className="text-sm hover:underline opacity-70 hover:opacity-100 truncate block">
                                {source.title}
                            </a>
                        </li>
                    ))}
                </ul>
            </div>
        )}
      </main>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Sparkles, ArrowRight, Book, History, X, Loader2 } from 'lucide-react';
import { fetchArticleContent } from './services/geminiService';
import { ArticleData, AppState, ReaderTheme, ReadingHistoryItem } from './types';
import { ArticleView } from './components/ArticleView';
import { ThemeToggle } from './components/ThemeToggle';

export default function App() {
  const [url, setUrl] = useState('');
  const [appState, setAppState] = useState<AppState>(AppState.IDLE);
  const [articleData, setArticleData] = useState<ArticleData | null>(null);
  const [errorMsg, setErrorMsg] = useState('');
  const [theme, setTheme] = useState<ReaderTheme>(ReaderTheme.LIGHT);
  const [history, setHistory] = useState<ReadingHistoryItem[]>([]);
  const [showHistory, setShowHistory] = useState(false);

  // Load history from local storage on mount
  useEffect(() => {
    const savedHistory = localStorage.getItem('clearview_history');
    if (savedHistory) {
      setHistory(JSON.parse(savedHistory));
    }
  }, []);

  const saveToHistory = (article: ArticleData) => {
    const newItem: ReadingHistoryItem = {
      id: Date.now().toString(),
      url: article.url,
      title: article.title,
      timestamp: Date.now()
    };

    // Filter out duplicates and keep last 20
    const updatedHistory = [newItem, ...history.filter(h => h.url !== article.url)].slice(0, 20);
    setHistory(updatedHistory);
    localStorage.setItem('clearview_history', JSON.stringify(updatedHistory));
  };

  const handleRead = async (inputUrl: string) => {
    if (!inputUrl.trim()) return;

    // Auto-prepend https if missing
    let finalUrl = inputUrl.trim();
    if (!/^https?:\/\//i.test(finalUrl)) {
        finalUrl = 'https://' + finalUrl;
    }

    setAppState(AppState.LOADING);
    setErrorMsg('');

    try {
      const data = await fetchArticleContent(finalUrl);
      setArticleData(data);
      saveToHistory(data);
      setAppState(AppState.READING);
    } catch (err: any) {
      console.error(err);
      setErrorMsg(err.message || "Failed to process article");
      setAppState(AppState.ERROR);
    }
  };

  const handleBack = () => {
    setAppState(AppState.IDLE);
    setArticleData(null);
    setUrl('');
  };

  const deleteHistoryItem = (id: string, e: React.MouseEvent) => {
    e.stopPropagation();
    const newHistory = history.filter(h => h.id !== id);
    setHistory(newHistory);
    localStorage.setItem('clearview_history', JSON.stringify(newHistory));
  };

  // Render Reading Mode
  if (appState === AppState.READING && articleData) {
    return <ArticleView article={articleData} theme={theme} onBack={handleBack} />;
  }

  // Render Landing/Input Mode
  return (
    <div className={`min-h-screen flex flex-col transition-colors duration-300 ${
      theme === ReaderTheme.DARK ? 'bg-gray-900 text-white' : 
      theme === ReaderTheme.SEPIA ? 'bg-sepia-50 text-sepia-900' : 'bg-gray-50 text-gray-900'
    }`}>

      {/* Top Bar */}
      <div className="absolute top-4 right-4 z-10">
        <ThemeToggle currentTheme={theme} onThemeChange={setTheme} />
      </div>

      <div className="flex-1 flex flex-col items-center justify-center p-6 sm:p-12 max-w-5xl mx-auto w-full">

        {/* Brand */}
        <div className="text-center mb-12 animate-fade-in-up">
          <div className="inline-flex items-center justify-center p-3 rounded-2xl bg-gradient-to-tr from-blue-600 to-indigo-500 text-white shadow-lg mb-6">
            <Sparkles size={32} />
          </div>
          <h1 className="text-4xl md:text-6xl font-serif font-bold mb-4 tracking-tight">
            ClearView Reader
          </h1>
          <p className="text-lg md:text-xl opacity-60 max-w-lg mx-auto font-light">
            Read articles without friction. Paste a URL to get a clean, distraction-free, AI-enhanced reading experience.
          </p>
        </div>

        {/* Input Card */}
        <div className="w-full max-w-2xl relative group animate-fade-in-up" style={{ animationDelay: '0.1s' }}>
          <div className={`absolute -inset-1 rounded-2xl blur opacity-25 group-hover:opacity-50 transition duration-1000 group-hover:duration-200 ${
              theme === ReaderTheme.DARK ? 'bg-gradient-to-r from-blue-600 to-indigo-600' : 'bg-gradient-to-r from-blue-400 to-indigo-400'
          }`}></div>

          <div className={`relative rounded-2xl p-2 shadow-xl ${
               theme === ReaderTheme.DARK ? 'bg-gray-800' : 'bg-white'
          }`}>
             <form 
                onSubmit={(e) => { e.preventDefault(); handleRead(url); }}
                className="flex items-center"
             >
                <input
                  type="text"
                  placeholder="Paste article URL here..."
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                  className={`flex-1 p-4 rounded-xl text-lg outline-none bg-transparent placeholder-opacity-40 ${
                    theme === ReaderTheme.DARK ? 'text-white placeholder-gray-500' : 'text-gray-900 placeholder-gray-400'
                  }`}
                />
                <button
                  type="submit"
                  disabled={appState === AppState.LOADING || !url}
                  className="bg-blue-600 hover:bg-blue-700 text-white p-3 rounded-xl transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed shadow-md flex items-center gap-2 font-medium px-6"
                >
                  {appState === AppState.LOADING ? (
                    <Loader2 className="animate-spin" />
                  ) : (
                    <>
                      <span>Read</span>
                      <ArrowRight size={18} />
                    </>
                  )}
                </button>
             </form>
          </div>
        </div>

        {/* Error Message */}
        {appState === AppState.ERROR && (
           <div className="mt-8 p-4 bg-red-100 text-red-700 rounded-lg max-w-md text-center border border-red-200 animate-fade-in-up">
              <p className="font-medium">Error processing article</p>
              <p className="text-sm mt-1 opacity-80">{errorMsg}</p>
           </div>
        )}

        {/* Recent History Toggle */}
        <div className="mt-16 w-full max-w-2xl animate-fade-in-up" style={{ animationDelay: '0.2s' }}>
          <button 
            onClick={() => setShowHistory(!showHistory)}
            className="flex items-center gap-2 text-sm font-semibold uppercase tracking-wider opacity-50 hover:opacity-100 transition-opacity mx-auto mb-6"
          >
             <History size={16} />
             <span>Recent Reads</span>
          </button>

          {showHistory && (
             <div className={`rounded-xl border overflow-hidden transition-all ${
                 theme === ReaderTheme.DARK ? 'border-gray-700 bg-gray-800/50' : 
                 theme === ReaderTheme.SEPIA ? 'border-sepia-200 bg-sepia-100/50' : 'border-gray-200 bg-white'
             }`}>
                {history.length === 0 ? (
                    <div className="p-8 text-center opacity-40">No history yet</div>
                ) : (
                    <div className="divide-y divide-opacity-10 divide-current max-h-60 overflow-y-auto">
                        {history.map((item) => (
                            <div 
                                key={item.id}
                                onClick={() => handleRead(item.url)}
                                className={`p-4 flex items-center justify-between group cursor-pointer ${
                                    theme === ReaderTheme.DARK ? 'hover:bg-gray-700' : 'hover:bg-gray-50'
                                }`}
                            >
                                <div className="flex items-center gap-3 overflow-hidden">
                                    <div className="p-2 rounded-lg bg-current opacity-5">
                                        <Book size={16} />
                                    </div>
                                    <div className="min-w-0">
                                        <p className="font-medium truncate">{item.title}</p>
                                        <p className="text-xs opacity-50 truncate">{item.url}</p>
                                    </div>
                                </div>
                                <button 
                                    onClick={(e) => deleteHistoryItem(item.id, e)}
                                    className="p-2 rounded-full opacity-0 group-hover:opacity-50 hover:!opacity-100 hover:bg-red-100 hover:text-red-600 transition-all"
                                >
                                    <X size={14} />
                                </button>
                            </div>
                        ))}
                    </div>
                )}
             </div>
          )}
        </div>

      </div>

      <footer className="p-6 text-center opacity-30 text-xs">
         <p>Powered by Gemini 3  Designed for clarity</p>
      </footer>
    </div>
  );
}

import React, { useState } from 'react';
import ReactMarkdown from 'react-markdown';
import { ArticleData, ReaderTheme } from '../types';
import { ArrowLeft, BookOpen, ExternalLink, MessageSquare, FileSpreadsheet, Check, Loader2, Copy, Download } from 'lucide-react';
import { askQuestionAboutArticle } from '../services/geminiService';
import { saveArticleToSheet } from '../services/sheetService';

interface ArticleViewProps {
  article: ArticleData;
  theme: ReaderTheme;
  onBack: () => void;
}

export const ArticleView: React.FC<ArticleViewProps> = ({ article, theme, onBack }) => {
  const [showChat, setShowChat] = useState(false);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [isAsking, setIsAsking] = useState(false);

  // Sheet saving states
  const [isSaving, setIsSaving] = useState(false);
  const [saveSuccess, setSaveSuccess] = useState(false);

  // Copy state
  const [copied, setCopied] = useState(false);

  // Theme configuration
  const themeClasses = {
    [ReaderTheme.LIGHT]: 'bg-white text-gray-900 selection:bg-yellow-200',
    [ReaderTheme.DARK]: 'bg-gray-900 text-gray-300 selection:bg-blue-900',
    [ReaderTheme.SEPIA]: 'bg-sepia-50 text-sepia-900 selection:bg-sepia-200',
  };

  const proseClasses = {
    [ReaderTheme.LIGHT]: 'prose-stone prose-lg',
    [ReaderTheme.DARK]: 'prose-invert prose-lg',
    [ReaderTheme.SEPIA]: 'prose-stone prose-lg marker:text-sepia-800 prose-headings:text-sepia-900 prose-a:text-sepia-800',
  };

  const handleAsk = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!question.trim()) return;

    setIsAsking(true);
    setAnswer('');
    try {
        const result = await askQuestionAboutArticle(article.content, question);
        setAnswer(result);
    } catch (err) {
        setAnswer("Failed to get answer.");
    } finally {
        setIsAsking(false);
    }
  };

  const handleSaveToSheet = async () => {
    setIsSaving(true);
    try {
        await saveArticleToSheet(article);
        setSaveSuccess(true);
        setTimeout(() => setSaveSuccess(false), 3000);
    } catch (error: any) {
        console.error(error);
        if (error.message === 'CANCELLED_BY_USER') {
            // User cancelled the prompt, do nothing
            return;
        }
        alert("Failed to save to Google Sheet. " + (error.message || "Ensure popups are allowed and Client ID is configured."));
    } finally {
        setIsSaving(false);
    }
  };

  const handleCopy = async () => {
    try {
        const textToCopy = `# ${article.title}\n\n${article.content}`;
        await navigator.clipboard.writeText(textToCopy);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
    } catch (err) {
        console.error('Failed to copy', err);
    }
  };

  const handleExportPDF = (e?: React.MouseEvent) => {
    if (e) {
      e.preventDefault();
      e.stopPropagation();
    }

    // 1. Generate filename: first 6 words
    const words = article.title.split(/\s+/).filter(w => w.length > 0);
    const filenameBase = words.slice(0, 6).join(' ');

    // 2. Temporarily change document title to control PDF filename
    const originalTitle = document.title;
    document.title = filenameBase;

    // 3. Trigger print with a small delay to allow title change to register
    // and ensure we are outside the React event cycle
    setTimeout(() => {
        if (typeof window.print === 'function') {
            window.print();
        } else {
            alert("Printing is not supported in this browser.");
        }

        // 4. Restore original title
        // Note: Some browsers might block execution until print dialog is closed
        document.title = originalTitle;
    }, 100);
  };

  return (
    <div className={`min-h-screen transition-colors duration-500 ${themeClasses[theme]}`}>
      {/* Navbar for Reader */}
      <div className={`sticky top-0 z-20 backdrop-blur-md border-b print:hidden ${
          theme === ReaderTheme.DARK ? 'border-gray-800 bg-gray-900/80' : 
          theme === ReaderTheme.SEPIA ? 'border-sepia-200 bg-sepia-50/80' : 'border-gray-200 bg-white/80'
      }`}>
        <div className="max-w-4xl mx-auto px-4 h-16 flex items-center justify-between">
          <button 
            onClick={onBack}
            className="flex items-center space-x-2 opacity-70 hover:opacity-100 transition-opacity font-medium"
          >
