      // 2. Temporarily change document title to control PDF filename
      const originalTitle = document.title;
      document.title = filenameBase;

      // 3. Trigger print directly. 
      // Note: window.print() is blocking in most browsers, so we don't need a timeout
      // to change the title back immediately after.
      window.print();

      // 4. Restore original title
      document.title = originalTitle;
    } catch (err) {
      console.error("Print dialog failed", err);
      alert("Could not open the print dialog. Your browser might be blocking it.");
    } finally {
      // Small delay for UI polish
      setTimeout(() => setIsExporting(false), 1000);
    }
  };

  return (
