              ),
              img: ({node, ...props}) => (
                 // eslint-disable-next-line jsx-a11y/alt-text
                 <img className="rounded-xl shadow-md my-8 w-full object-cover max-h-[500px] print:shadow-none print:rounded-none" {...props} />
              )
            }}
          >
