        </div>
      </main>
    </div>
  );
};

import { GoogleGenAI } from "@google/genai";
import { ArticleData } from "../types";

// Initialize the client strictly following guidelines using process.env.API_KEY directly
const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

/**
 * Fetches and reconstructs an article using Gemini with Google Search Grounding.
 * Uses Pro model for better extraction of paywalled content and images.
 */
export const fetchArticleContent = async (url: string): Promise<ArticleData> => {
  // Using Pro for better reasoning and higher probability of finding full text and images
  const modelId = "gemini-3-pro-preview"; 

  const prompt = `
    ROLE: You are an elite Content Extraction Specialist.
    GOAL: Reconstruct the full article at the provided URL, bypassing paywalls or login walls by synthesizing data found via Google Search.

    TARGET URL: ${url}

    **STRICT OUTPUT RULES:**
    1. START immediately with the metadata block. No conversational filler.
    2. Provide the complete article text. Maintain the original flow and nuance.
    3. Include a source attribution: "Source: [Original Article Title](${url})" at the start of the body.
    4. Include direct URLs to the primary hero image and body images using Markdown.
    5. Formatting: Use clean Markdown. Use ## for headers.

    **OUTPUT FORMAT (MANDATORY):**
    ---
    title: [Exact Article Title]
    author: [Author Name]
    siteName: [Publication Name]
    ---

    ![Hero Image Description](Direct Hero Image URL)

    Source: [Link to Original Article](${url})

    ---

    [Full Article Body in Markdown]
  `;

  try {
    const response = await ai.models.generateContent({
      model: modelId,
      contents: prompt,
      config: {
        tools: [{ googleSearch: {} }],
      },
    });

    let responseText = response.text || "";

    if (!responseText && response.candidates && response.candidates.length > 0) {
        const parts = response.candidates[0].content?.parts;
        if (parts) {
            responseText = parts
                .filter((p: any) => p.text)
                .map((p: any) => p.text)
                .join('');
        }
    }

    if (!responseText) {
        throw new Error("No content received from AI.");
    }

    // Improved Parsing: Handle potential text before the first '---'
    const frontmatterMatch = responseText.match(/---([\s\S]*?)---([\s\S]*)/);

    let title = "";
    let author = "Unknown";
    let siteName = "Web";
    let content = responseText;

    if (frontmatterMatch) {
        const metadataStr = frontmatterMatch[1];
        content = frontmatterMatch[2].trim();

        const getMeta = (key: string) => {
            const regex = new RegExp(`^${key}:\\s*(.*)$`, 'mi');
            const match = metadataStr.match(regex);
            return match ? match[1].trim() : null;
        };

        title = (getMeta('title') || "").replace(/^"|"$/g, '');
        author = (getMeta('author') || "Unknown").replace(/^"|"$/g, '');
        siteName = (getMeta('siteName') || "Web").replace(/^"|"$/g, '');
    }

    // Fallback Title Extraction: If frontmatter failed or title is empty, look for Markdown headers
    if (!title) {
        const h1Match = content.match(/^#\s+(.*)$/m);
        const h2Match = content.match(/^##\s+(.*)$/m);
        if (h1Match) title = h1Match[1].trim();
        else if (h2Match) title = h2Match[1].trim();
        else {
            // Last resort: extract from the URL domain or a placeholder
            try {
                const domain = new URL(url).hostname.replace('www.', '');
                title = `Article from ${domain}`;
            } catch {
                title = "Untitled Article";
            }
        }
    }

    const sources = response.candidates?.[0]?.groundingMetadata?.groundingChunks?.map((chunk: any) => {
        return chunk.web ? { title: chunk.web.title, uri: chunk.web.uri } : null;
    }).filter((item: any) => item !== null) || [];

    return { 
        title: title.replace(/[#*]/g, '').trim(), // Clean any accidental markdown from title
        content, 
        author, 
        siteName, 
        url, 
        sources 
    };

  } catch (error: any) {
    console.error("Gemini Service Error:", error);
    throw new Error(error.message || "Failed to reconstruct article content.");
  }
};

