/**
 * Fetches and reconstructs an article using Gemini with Google Search Grounding.
 * Uses Pro model for better extraction of paywalled content and images.
 */
export const fetchArticleContent = async (url: string): Promise<ArticleData> => {
  const modelId = "gemini-3-pro-preview"; 

  const prompt = `
    ROLE: You are an elite Investigative Content Specialist.
    GOAL: Reconstruct the ABSOLUTE FULL article at the provided URL. 

    TARGET URL: ${url}

    **PAYWALL CHALLENGE:**
    The target site (e.g., The Information, Bloomberg, WSJ) may have a strict paywall. 
    1. Use Google Search to find the full text across multiple sources (syndications, shared snippets, or archive records).
    2. DO NOT provide a summary. DO NOT truncate the content. 
    3. Synthesize the complete narrative from start to finish. If the article is 2000 words, I want 2000 words.

    **MANDATORY OUTPUT FORMAT:**
    You MUST output a YAML frontmatter block followed by the article content in Markdown.

    ---
    title: [Exact Article Title]
    author: [Author Name]
    siteName: [Publication Name]
    ---

    Source: [Original Article](${url})

    ![Feature Image Description](Direct_Public_Image_URL)

    [Full Article Body in Markdown with subheaders and inline images]

    **STRICT RULES:**
    1. The "title" in frontmatter must be the actual original headline.
    2. The first line of the body MUST be: "Source: [Title](${url})".
    3. Use Google Search to find high-resolution, public image URLs belonging to the article.
    4. Start immediately with '---'. No "Sure, here is..." filler.
  `;

  try {
    const response = await ai.models.generateContent({
      model: modelId,
      contents: prompt,
      config: {
        tools: [{ googleSearch: {} }],
      },
    });

    let responseText = response.text || "";

    if (!responseText && response.candidates && response.candidates.length > 0) {
        const parts = response.candidates[0].content?.parts;
        if (parts) {
            responseText = parts
                .filter((p: any) => p.text)
                .map((p: any) => p.text)
                .join('');
        }
    }

    if (!responseText) {
        throw new Error("No content received from AI. The paywall might be too strong for current search results.");
    }

    // Advanced Parsing Logic
    const frontmatterMatch = responseText.match(/---([\s\S]*?)---([\s\S]*)/);

    let title = "";
    let author = "Unknown";
    let siteName = "Web";
    let content = responseText;

    if (frontmatterMatch) {
        const metadataStr = frontmatterMatch[1];
        content = frontmatterMatch[2].trim();

        const getMeta = (key: string) => {
            const regex = new RegExp(`^${key}:\\s*(.*)$`, 'mi');
            const match = metadataStr.match(regex);
            return match ? match[1].trim() : null;
        };

        title = (getMeta('title') || "").replace(/^["']|["']$/g, '');
        author = (getMeta('author') || "Unknown").replace(/^["']|["']$/g, '');
        siteName = (getMeta('siteName') || "Web").replace(/^["']|["']$/g, '');
    }

    // Fallback Title Extraction
    if (!title || title.toLowerCase() === 'article view' || title.toLowerCase() === 'untitled article') {
        const h1Match = content.match(/^#\s+(.*)$/m);
        const h2Match = content.match(/^##\s+(.*)$/m);
        if (h1Match) title = h1Match[1].trim();
        else if (h2Match) title = h2Match[1].trim();
        else {
            try {
                const urlObj = new URL(url);
                const pathParts = urlObj.pathname.split('/').filter(p => p.length > 2);
                if (pathParts.length > 0) {
                    title = pathParts[pathParts.length - 1].replace(/-/g, ' ').replace(/\.[^/.]+$/, "");
                    title = title.charAt(0).toUpperCase() + title.slice(1);
                } else {
                    title = urlObj.hostname;
                }
            } catch {
                title = "Untitled Article";
            }
        }
    }

    title = title.replace(/[*_#]/g, '').trim();

    const sources = response.candidates?.[0]?.groundingMetadata?.groundingChunks?.map((chunk: any) => {
        return chunk.web ? { title: chunk.web.title, uri: chunk.web.uri } : null;
    }).filter((item: any) => item !== null) || [];

    return { 
        title, 
        content, 
        author, 
        siteName, 
        url, 
        sources 
    };

  } catch (error: any) {
    console.error("Gemini Service Error:", error);
    throw new Error(error.message || "Failed to process article content.");
  }
};

/**
 * Chat with the article content.
 */
export const askQuestionAboutArticle = async (articleContent: string, question: string): Promise<string> => {
    try {
        const response = await ai.models.generateContent({
            model: "gemini-3-flash-preview",
            contents: `
                Context: The following is the content of an article extracted from a web search:
                ---
                ${articleContent.substring(0, 30000)} 
                ---
                User Question: ${question}
                Answer based strictly on the text provided.
            `
        });
        return response.text || "I couldn't generate an answer based on the current context.";
    } catch (e) {
        return "Error answering question.";
    }
};

